INSERT INTO `linksafe` (`id`, `titel`, `description`, `url`, `image`, `category`, `advertising`, `clicks`, `date`) VALUES
(1, 'free 3d', '„free3d“ ist eine umfangreiche Sammlung an 3d-Modellen in unterschiedlichen Formaten. es werden auch Modelle mit Animationen bzw. Skelett angeboten, sowie auch speziell für die Computerspiele-Entwicklung erstellte Modelle.', 'https://free3d.com', 'teaser_verweis_free3d.jpg', '3D-Material', 'false', 12, ''),
(2, 'Freizeitmagazin Royale', 'Die deutschen Freizeitmagazine und die dahinter stehenden Eigentümer werden hier mit ihren eigenen Mitteln des Journalismus konfrontiert und entblößt.', 'https://freizeitmagazinroyale.de/', 'teaser_verweis_fmroyal..jpg', 'Aufklärung', 'false', 37, ''),
(3, 'Frontext Files', 'Diese Seite „frontexfiles.eu“ ist eine Aufklärung des „ZDF Magazin Royale“ über die Aktivitäten der EU und dem Unternehmen Frontex, welches dafür eingesetzt wird Menschenrechtsverletzungen aller art im Auftrag der Europäischen Union durchzuführen.', 'https://frontexfiles.eu/', 'teaser_verweise_frontexfiles.jpg', 'Aufklärung', 'false', 11, ''),
(4, 'Geld und Wohlstand', 'In diesem Podcast sprechen Ole Nymoen und Wolfgang m. Schmitt über Geld und Wohlstand und klären über ökonomische Themen und Zusammenhänge auf.', 'https://wohlstandfueralle.podigee.io/', 'teaser_verweis_wohlstandfueralle.jpg', 'Aufklärung', 'false', 56, ''),
(5, 'Geld für die Welt', '„Geld für die Welt“ ist ein Newsletter von Maurice Höfgen, einem Ökonom und Autor des Buches „Mythos Geldknappheit“, der aufzeigt, wie falsch die etablierte Ökonomie denkt und handelt. Neben diesem Newsletter gibt es auch noch einen „YouTube“-Kanal mit gleichem Titel.', 'https://mauricehoefgen.substack.com/', 'teaser_verweis_geldfuerdiewelt.jpg', 'Aufklärung', 'false', 98, ''),
(6, 'unsplash', 'Bei \"unsplash\" sind alle Inhalte kostenlos und frei zugänglich. die Inhalte können für private und kommerzielle Projekte verwendet werden und es gibt keine Bedingungen, die eine Nutzung erlauben.', 'https://unsplash.com/', 'teaser_verweise_unsplash.jpg', 'Bildmaterial', 'false', 6, ''),
(7, 'pexels', 'Schöne und kostenlose Fotos und Videos, die ohne Einschränkungen nutzbar sind. Auch kostenlose Videos und mehr sind so verfügbar.', 'https://www.pexels.com/de-de/', 'teaser_verweise_pexels.jpg', 'Bildmaterial', 'false', 63, ''),
(8, 'Free Nature Stock', 'Sehr viele schöne Naturbilder und Videos für den privaten und kommerziellen Gebrauch finden Sie auf dieser Webseite, die Inhalte werden unter einer  \"CC0\" Lizenz veröffentlicht.', 'https://freenaturestock.com/', 'teaser_verweise_freenaturestock.jpg', 'Bildmaterial', 'false', 29, ''),
(9, 'Morgue File', '\"Morgue File\" bietet eine große Auswahl an kostenlosen Bildern, Videos, Vektorgrafiken und Templates.', 'https://morguefile.com/', 'teaser_verweise_morguefile.jpg', 'Bildmaterial', 'false', 21, ''),
(10, 'Public Domain Pictures', 'Ganz viele Bilder für umsonst und jeden erdenklichen Zweck. \"Public Domain\" bezeichnet gemeinfreie Inhalte, d. h. der Urheber verzichtet auf seine Rechte an dem/den Werk(en).', 'https://www.publicdomainpictures.net/', 'teaser_verweise_publicdomainpictures.jpg', 'Bildmaterial', 'false', 183, ''),
(11, 'Stocksnap', 'Kostenlose Bilder unter der \"CC0\"-Lizenz, frei von Urheberrechtsbedingungen.', 'https://stocksnap.io', 'teaser_verweise_stocksnap.io_.jpg', 'Bildmaterial', 'false', 92, ''),
(12, 'Clean PNG', 'Viele wunderschöne, kostenlose, hochaufgelöste und hintergrundfreie PNG-Dateien für private und kommerzielle Projekte.', 'https://de.cleanpng.com/', 'teaser_verweise_cleanpng.jpg', 'Bildmaterial', 'false', 43, ''),
(13, 'Style Stock', 'Kostenlose feminine Fotos für ihre Projekt. in Druckauflösung und eben kostenlos.', 'https://styledstock.co/', 'teaser_verweise_styledstock.jpg', 'Bildmaterial', 'false', 13, ''),
(14, 'BURST', 'Laden Sie kostenlose und hoch aufgelöste Fotos kostenlos für Ihre Projekte herunter.', 'https://burst.shopify.com/', 'teaser_verweise_burst.jpg', 'Bildmaterial', 'false', 11, ''),
(15, 'freestocks', 'Kostenlose Bilder für persönlichen oder kommerziellen Projekte.', 'https://freestocks.org/', 'teaser_verweise_freestocks.org_.jpg', 'Bildmaterial', 'false', 17, ''),
(16, 'Picography', 'Kostenlose Bilder für jeden Zweck: lizenzfrei und hoch aufgelöst.', 'https://picography.co/', 'teaser_verweise_picography.jpg', 'Bildmaterial', 'false', 21, ''),
(17, 'FOCA Stock', 'Kostenlose Fotos, Videos und Templates für kommerzielle Zwecke. (\"CC0\"-Lizenz)', 'https://focastock.com/', 'teaser_verweise_focastock.jpg', 'Bildmaterial', 'false', 195, ''),
(18, 'picjumbo', 'Wunderschöne kostenlose Fotos und Hintergründen in hoher Auflösung für den privaten und kommerziellen Gebrauch.', 'https://picjumbo.com/', 'teaser_verweise_picjumbo.jpg', 'Bildmaterial', 'false', 0, ''),
(19, 'pixabay', 'Über 2.5 Millionen Bilder in hoher Qualität und Auflösung, sogar Videos und Musik sind hier kostenlos erhältlich.', 'https://pixabay.com/', 'teaser_verweise_pixabay.jpg', 'Bildmaterial', 'false', 9, ''),
(20, 'NASA Images', 'Audio-, Video- und Bilddateien der NASA kostenlos für eigene Projekte verwenden.', 'https://images.nasa.gov/', 'teaser_verweise_nasa.jpg', 'Bildmaterial', 'false', 34, ''),
(21, 'Life of Pix', 'Kostenlos hochaufgelöste Fotos für jeden Verwendungszweck.', 'https://www.lifeofpix.com/', 'teaser_verweise_freehightresolution.jpg', 'Bildmaterial', 'false', 76, ''),
(22, 'Little Visuals', 'Super viele Fotos kostenlos für den kommerziellen Gebrauch.', 'https://littlevisuals.co/', 'teaser_verweise_littlevisuals.jpg', 'Bildmaterial', 'false', 3, ''),
(23, 'twnsnd', 'Eine Fotosammlung uralter Fotografien für den persönlichen Gebrauch.', 'https://nos.twnsnd.co/', 'teaser_verweise_newoldstock.jpg', 'Bildmaterial', 'false', 1, ''),
(24, 'Anyrgb', 'Sehr viele schöne Cliparts für diverse Zwecke.', 'https://www.anyrgb.com/', 'teaser_verweise_anyrgb.jpg', 'Bildmaterial', 'false', 1, ''),
(25, 'Skuawk', 'Viele lizenzfreie Fotos unter einer \"CC0\"-Lizenz.', 'https://skuawk.com/', 'teaser_verweise_skuawk.jpg', 'Bildmaterial', 'false', 6, ''),
(26, 'stockvault', 'Fotos, Texturen und Illustrationen (es gelten die dem Bild beigefügten Lizenzbedingungen) kostenlos herunterladbar.', 'https://www.stockvault.net/', 'teaser_verweise_stockvault.jpg', 'Bildmaterial', 'false', 8, ''),
(27, 'Foodiesfeed', 'Sehr als 1700 kostenlose Fotos von Mahlzeiten und Nahrungsmitteln warten auf Sie.', 'https://www.foodiesfeed.com/', 'teaser_verweise_foodiesfeed.jpg', 'Bildmaterial', 'false', 2, ''),
(28, 'GratisoGraphy', 'Die Bilder hier gibt es ohne Urheberrechtsbedingungen kostenlos.', 'https://gratisography.com/', 'teaser_verweise_gatisography.jpg', 'Bildmaterial', 'false', 62, ''),
(29, 'Pikwizard', 'Hochwertige, kostenlose Stockfotos: atemberaubende Bibliothek mit über 1 Million Bildern und Videos. gebührenfrei und sicher für die kommerzielle Nutzung, ohne dass eine Namensnennung erforderlich ist.', 'https://pikwizard.com/', 'teaser_verweise_pikwizard.jpg', 'Bildmaterial', 'false', 19, ''),
(30, 'Pepper and Carrot', '\"Pepper and Carrot\" ist eine Comicserie von David Revoy, einem Künstler, der überwiegend für digitale Kunstwerke freie software verwendet.', 'https://www.peppercarrot.com/', 'teaser_pepperandcarrot.jpg', 'Comic', 'false', 297, ''),
(31, 'Open Game Art', '„opengameart.org“ bietet eine große Auswahl an kostenlosen und frei verfügbaren „Assets“ für Computerspiele. von Musik bis hin zu 3D-Modellen.', 'https://opengameart.org/', 'teaser_verweis_opengameartorg.jpg', 'Computerspielmaterial', 'false', 37, ''),
(32, 'ZFA Medien', 'Der zentrale Fachausschuss für Medien ist die Anlaufstelle für Fachinformationen aller in der Medienbranche schaffenden.', 'https://zfamedien.de/', 'teaser_verweis_zfamedien.jpg', 'Information', 'false', 13, ''),
(33, 'ALT Codes', 'Eine Übersicht über die Zeichen, die via Tastatur mit der Alt-Taste erzeugt werden können.', 'https://www.alt-codes.net/', 'teaser_verweis_altcodes.jpg', 'Information', 'false', 19, ''),
(34, 'art-hm', 'Hursched Murodow´s Webseite: \"Kunst und Gestaltung sind seit frühester Jugend bestimmend für mich. angefangen hat alles mit \"Walt-Disney-Figuren\", die ich mit Bleistift gezeichnet habe ...\"', 'https://art-hm.de/', 'teaser_art-hm.com_.jpg', 'Künstler', 'false', 29, ''),
(35, 'Free Music Archive', 'Eine große Auswahl an kostenloser Musik für YouTube, Facebook und sonst noch was erwarten sie hier.', 'https://freemusicarchive.org/home', 'teaser_verweise_freemusicarchive.jpg', 'Musikmaterial', 'false', 11, ''),
(36, 'jami', '„jami“ ist eine freie und universelle Kommunikationsplattform, die die Privatsphäre und Freiheit der Benutzer schützt.', 'https://jami.net/', 'teaser_verweis_jami.jpg', 'Opensource', 'false', 61, ''),
(37, 'BeeRef', '„BeeRef“ ist ein kleines einfaches Programm um Referenzbilder anzuzeigen, zu arrangieren und minimal in den kreativen Arbeitsfluss einzugreifen.', 'https://beeref.org/', 'teaser_verweis_beeref.jpg', 'Opensource', 'false', 3, ''),
(38, 'Storyboarder', 'Dieses Programm macht es möglich einfach Geschichten zu visualisieren, die als Grundlage für Comics, Animationen oder Zwischensequenzen dienen.', 'https://wonderunit.com/storyboarder/', 'teaser_verweis_storyboarder.jpg', 'Opensource', 'false', 33, ''),
(39, 'Z-Anatomy', '„Z-Anatomy“ ist kein Programm, sondern eine Blender-Datei, die mit Blender genutzt werden kann. Es ist ein freier 3D-Atlas des menschlichen Körpers, der auch als App für das Handy verfügbar ist. ', 'https://www.z-anatomy.com/', 'teaser_verweis_zanatomy.jpg', 'Opensource', 'false', 39, ''),
(40, 'Matrial Maker', 'Um physikalisch basierende Anzeigen von Materialien in 3D-Anwendungen zu entwickeln, bietet sich „Material Maker“ an.', 'https://www.materialmaker.org/', 'teaser_verweis_matrialmaker.jpg', 'Opensource', 'false', 12, ''),
(41, 'FontForge', 'mit „FontForge“ lassen sich Fonts-Dateien erzeugen und erstellen. dabei bietet das Programm zahlreiche Möglichkeiten zur Bearbeitung der Topografie.', 'https://fontforge.org/', 'teaser_verweis_fontforge.jpg', 'Opensource', 'false', 79, ''),
(42, 'Milton', '„Milton“ ist ein kostenloses Zeichenprogramm mit einer unendlichen Leinwand, ebenen und einfacher Handhabung.', 'https://www.miltonpaint.com/', 'teaser_verweis_miltonpaint.jpg', 'Opensource', 'false', 43, ''),
(43, 'dupeGuru', 'Um Duplikate von Dateien zu finden, die sich auf der Festplatte verstecken und Speicherplatz unnötig verbrauchen, gibt es „dupeGuru“.', 'https://dupeguru.voltaicideas.net/', 'teaser_verweis_dupeguru.jpg', 'Opensource', 'false', 52, ''),
(44, 'overGrive', 'Der „google-Drive“ wird nicht nativ von „google“ unter „Linux“ unterstützt. „overGrive“ füllt diese Lücke.', 'https://www.thefanclub.co.za/overgrive/', 'teaser_verweis_overgrive.jpg', 'Opensource', 'false', 12, ''),
(45, 'Effekseer', 'Mit diesem Programm können sie leicht und schnell wundervolle Partikel-Effekte zu erzeugen, welche sich dann in „Unreal“, „Unity“ usw. verwenden lassen.', 'https://effekseer.github.io/en/index.html', 'teaser_verweis_effekseer.jpg', 'Opensource', 'false', 79, ''),
(46, 'openElement', '„openElement“ ist ein Editor zur Entwickelung und gestalten von Webseiten nach dem Prinzip: „was du siehst, bekommst du“.', 'http://openelement.uk/index.html', 'teaser_verweis_openelement.jpg', 'Opensource', 'false', 68, ''),
(47, 'Webcamoid', '„Webcamoid“ ist eine Anwendung, die in Zusammenarbeit mit einer „Webcam“ diverse Manipulationen zulässt.', 'https://webcamoid.github.io/', 'teaser_verweis_webcamoid.jpg', 'Opensource', 'false', 12, ''),
(48, 'Tahoma 2D', '„Tahoma 2D“ ist eine kostenlose und freie Anwendung zum Erzeugen von 2D Bewegungsanimationen.', 'https://tahoma2d.org/', 'teaser_verweis_tahoma2d.jpg', 'Opensource', 'false', 63, ''),
(49, 'Alicevision', 'Diese software ist eine Anwendung zur Fotogrammetrie (Wissenschaft der Messung von Fotografien), sie ist quelloffen und kostenlos.', 'https://alicevision.org/', 'teaser_verweis_alicevision.jpg', 'Opensource', 'false', 27, ''),
(50, 'ArmorPaint', '„ArmorPaint“ ist eine 3D-PBR-Malanwendung für Windows, Linux und macOS. Die kostenlose Variante muss selbst über den Quellcode kompiliert werden.', 'https://armorpaint.org/', 'teaser_verweis_amorpaint.jpg', 'Opensource', 'false', 103, ''),
(51, 'Krita', '„Krita“ ist die Anwendung, wenn es um zeichnen und malen geht. digitale Kunst ist dabei das Schlagwort, denn keine anderes Programm ist so vielseitig und nützlich.', 'https://krita.org/', 'teaser_verweis_krita.jpg', 'Opensource', 'false', 52, ''),
(52, 'Blender', '„Blender“ ist so umfangreich an Funktionen und Möglichkeiten für den Nutzer, dass es quasi den Industriestandard wie „Maya“ oder „Cinema4d“ abgelöst hat.', 'https://www.blender.org/', 'teaser_verweis_blender.jpg', 'Opensource', 'false', 43, ''),
(53, 'Godot Engine', 'Die „Godot Engine“ ist eine in sich geschlossene Entwicklungsumgebung mit Editoren und Skriptsprachen für Spiele aller art.', 'https://godotengine.org/', 'teaser_verweis_godotengine.jpg', 'Opensource', 'false', 22, ''),
(54, 'Inkscape', 'Das Vektorgrafikprogramm im quelloffenen und kostenlosen Bereich der Software, das keine wünsche für Knotenpunkt-Verschieber offen lässt.', 'https://inkscape.org/', 'teaser_verweis_inkscape.jpg', 'Opensource', 'false', 17, ''),
(55, 'Gimp', '„The GNU Image Manipulation Programm“ ist ein Programm zum Editieren von Fotos, quasi kann man Fotos so „gimpen“, dass sie wie „gefotoshoppt“ aussehen.', 'https://www.gimp.org/', 'teaser_verweis_gimp.jpg', 'Opensource', 'false', 51, ''),
(56, 'Scribus', 'Die Anwendung „Scribus“ ist eine DTP-Anwendung für angehende Schriftsetzer, welche sich die ersten Erkenntnisse rauf schaffen wollen.', 'https://www.scribus.net/', 'teaser_verweis_scribus.jpg', 'Opensource', 'false', 12, ''),
(57, 'MyPaint', '„MyPaint“ ist eine Zeichen- und Malanwendung, die auf einer unbegrenzten Leinwand schöpferische Fähigkeiten auf den Punkt bringt.', 'https://github.com/mypaint/mypaint/releases', 'teaser_verweis_mypaint.jpg', 'Opensource', 'false', 41, ''),
(58, 'Pencil2D', 'Um Animationen im 2D-Raum zu erzeugen, gibt es viele Anwendungen, nur wenige glänzen mit einer aufgeräumten und leicht zugänglichen Bedienung.', 'https://www.pencil2d.org/', 'teaser_verweis_pencil2d.jpg', 'Opensource', 'false', 4, ''),
(59, 'ephtracy', 'Unter „MagicaVoxel“ finden sich auch noch weite interessante Editoren für 3D-Objekte. Im Speziellen ist „MagicaVoxel“ aber ein Voxel 3D-Programm.', 'https://ephtracy.github.io/', 'teaser_verweis_magicavoxel.jpg', 'Opensource', 'false', 101, ''),
(60, 'Sweet Home 3D', 'Der Innenraumplaner „Sweet Home 3D“ bietet einen großen Funktionsumfang zum Konstruieren von Wohnräumen und sogar ganzen Häusern.', 'http://www.sweethome3d.com/', 'teaser_verweis_sweethome3d.jpg', 'Opensource', 'false', 5, ''),
(61, 'Birdfont', 'Ein Font-Editor mit dem üblichen Funktionsumfang und typischen Exportmöglichkeiten für Fonts glänzt.', 'https://birdfont.org/', 'teaser_verweis_birdfont.jpg', 'Opensource', 'false', 10, ''),
(62, 'Make Human', 'Eine Anwendung zum Generieren von Charakter-Polygonobjekten (Meshes) zur Endverwendung in anderen 3D-Programmen wie Blender.', 'http://www.makehumancommunity.org/', 'teaser_verweis_makehuman.jpg', 'Opensource', 'false', 25, ''),
(63, 'Free Texture Packer', '„Free Texture Packer“ packt viele einzelne Grafiken (Sprites) zu einer einzigen großen Grafik (Sprite Sheet) zusammen, sodass „Game Engines“ diese ressourcenschonend nutzen können.', 'http://free-tex-packer.com/', 'teaser_verweis_freetexturepacker.jpg', 'Opensource', 'false', 106, ''),
(64, 'Stellarium', '„Stellarium“ ist ein quelloffenes und kostenloses Programm, welches am Computer ein komplettes Planetarium simuliert.', 'http://stellarium.org/', 'teaser_verweis_stellarium.jpg', 'Opensource', 'false', 17, ''),
(65, 'FileZilla', 'Um auf „Webspace“ zugreifen zu können, um Dateien auszutauschen, benötigt man ein Programm, welches das „ftp“-Protokoll versteht. „FileZilla“ kann das und noch viel mehr.', 'https://filezilla-project.org/', 'teaser_verweis_filezilla.jpg', 'Opensource', 'false', 134, ''),
(66, 'OBS Studio', 'Um den Bildschirm aufzuzeichnen, zum Beispiel für einen „Stream“ bei diversen Videoplattformen im Internet, ist „OBS Studio“ unverzichtbar.', 'https://obsproject.com/', 'teaser_verweis_obsstudio.jpg', 'Opensource', 'false', 16, ''),
(67, 'Apophysis 7X', '„Apophysis 7X“ ist ein Erzeuger, Editor und Renderer für fraktale Visualisierungen (Flammen), welche zuweilen spektakuläre visuelle Fraktale generiert.', 'https://sourceforge.net/projects/apophysis7x/', 'teaser_verweis_apophysis7x.jpg', 'Opensource', 'false', 19, ''),
(68, 'MeshLab', 'Das quelloffene „MeshLab“ ist ein Programm zur Bearbeitung, Optimierung und Untersuchung von 3D-Hohlkörpermodellen (Meshes) aus 3D-Anwendungen.', 'https://www.meshlab.net/', 'teaser_verweis_meshlab.jpg', 'opensource', 'false', 11, ''),
(69, 'Cyotek Palette Editor', 'Dieses kleine nützliche Programm ermöglicht die Erstellung, das Konvertieren und die Bearbeitung von Farbpaletten für diverse Anwendungen.', 'https://www.cyotek.com/cyotek-palette-editor', 'teaser_verweis_cyotek.jpg', 'Opensource', 'false', 7, ''),
(70, 'Shotcut', 'Für die Bearbeitung von Videos gibt es eine großen Vielzahl an Anwendungen. „Shortcut“ ist ein quelloffener und kostenloser Vertreter aus diesem Spektrum von Anwendungen.', 'https://shotcut.org/', 'teaser_verweis_shotcut.jpg', 'Opensource', 'false', 18, ''),
(71, 'Kdenlive', '„Kdenlive“ ist ein kostenloser quelloffener Videoeditor, der laut Entwicklern auch sehr einfach anzuwenden ist und auch jedem Zweck dient.', 'https://kdenlive.org/', 'teaser_verweis_kdenlive.jpg', 'Opensource', 'false', 24, ''),
(72, 'yt-dl', '„YouTube dl“ macht das, was aller Wahrscheinlichkeit nach der Name verrät: Er lädt Inhalte von „YouTube“ herunter. ob das nun gut oder schlecht ist …?', 'https://yt-dl.org/', 'teaser_verweis_ytdl.jpg', 'Opensource', 'false', 42, ''),
(73, 'AnimeEffects', 'Hierbei handelt es sich um eine Anwendung, mit der 2D-Schlüsselbild-Animationen erzeugt werden können. es gibt sogar Polygon-Netze, laut Entwickler.', 'http://animeeffects.org/', 'teaser_verweis_animeeffects.jpg', 'Opensource', 'false', 2, ''),
(74, 'free cad web', '„Computer Aided Design“ bezeichnet computergestütztes Konstruieren, neben vielen Kostenpflichten Programmen aus diesem Bereich existiert auch noch das quelloffene und kostenlose „freecad“.', 'https://www.freecadweb.org/', 'teaser_verweis_freecad.jpg', 'Opensource', 'false', 25, ''),
(75, 'Sozi', '„Sozi“ ist eine Anwendung um Präsentationen hübsch und professionell aufzuwerten. „Sozi“ ist ein Editor und Abspieler für eben diese Präsentationen.', 'https://sozi.baierouge.fr/', 'teaser_verweis_sozi.jpg', 'Opensource', 'false', 36, ''),
(76, 'Lobbyland', '\"Lobbyland\" ist die Website des ehemaligen fraktionslosen Bundestagsabgeordneten Marco Bülow. er trat zuletzt in die Partei ein und war vormals SPD-Abgeordneter. in seiner Arbeit als Abgeordneter kritisierte er im Bundestag den Profillobbyismus, der die Demokratie zerstört.', 'https://lobbyland.de/', 'teaser_verweise_lobbyland.jpg', 'Politik', 'false', 321, ''),
(77, 'hackr.io', 'Für angehende Programmierer gibt es hier eine große Auswahl an Programmiersprachen, welche man lernen kann.', 'https://hackr.io/', 'teaser_verweise_hackr.io_.jpg', 'Programmierung', 'false', 21, ''),
(78, 'Blindtextgenerator', 'Der „Blindtextgenerator“ bietet eine Auswahl an verschiedenen Blindtexten für die Webseitenentwicklung, wo er noch nicht geschriebene Texte ersetzt.', 'https://www.blindtextgenerator.de/', 'teaser_verweis_blindtextgenerator.jpg', 'Sammlung', 'false', 34, ''),
(79, 'google webfonts helper', 'Beim „google webfonts helper“ sind alle Fonts von „google“ aufgelistet und können für den Gebrauch auf dem eigenen Webserver angepasst und vorbereitet werden.', 'https://google-webfonts-helper.herokuapp.com/fonts', 'teaser_verweis_googlewebfontshelper.jpg', 'Sammlung', 'false', 168, ''),
(80, 'Hero Patterns', 'Eine Sammlung wiederholbarer SVG-Hintergrundmustern, die sie in ihren Webprojekten verwenden können.', 'https://heropatterns.com/', 'teaser_verweis_heropatters.jpg', 'Sammlung', 'false', 3, ''),
(81, 'OpenVDB', 'VDB-Dateien können in 3D-Anwendungen verwendet werden bzw. dort importiert werden und stellen Effekte oder ähnliches zur Verfügung.', 'https://www.openvdb.org/', 'teaser_verweise_openvdb.jpg', 'Sammlung', 'false', 2, ''),
(82, 'Linotype', '„Linotype“ ist die erste Adresse für kommerzielle Schriftarten. neben aktuellen Informationen aus der Schriftarten-Designer-Szene finden sie auch weitere Informationen zu Schriftarten.', 'https://www.linotype.com/', 'teaser_verweis_linotype.jpg', 'Schriftarten', 'false', 8, ''),
(83, 'MyFonts', 'Auf dieser Seite dreht sich alles um Schriftarten, kostenlos für den privaten gebrauch oder kommerzielle Schriftarten zum Kaufen. Zudem kann man hier mit „what the font“ Schriftarten erkennen.', 'https://www.myfonts.com/', 'teaser_verweis_myfonts.jpg', 'Schriftarten', 'false', 54, ''),
(84, 'Libertine Fonts', 'Die „Libertine“ ist eine kostenlose Schriftart mit Serifen und Biolinum ist serifenlos, sie kommt mit Schriftschnitten wie „italic“ und „bold“ daher.', 'https://libertine-fonts.org/', 'teaser_verweis_libertine.jpg', 'Schriftarten', 'false', 86, ''),
(85, 'Font Library', 'Diese Sammlung von Schriftarten und Schriftschnitten veröffentlicht die Schriftdateien unter der „open font license“ (offene Schriftarten Lizenz). die Schriften sind qualitativ hochwertig und können sogar in Webseiten übernommen werden.', 'https://fontlibrary.org/', 'teaser_verweis_fontlibrary.jpg', 'Schriftarten', 'false', 62, ''),
(86, 'the league of moveable type', 'Diese Webseite bietet eine Auswahl exklusiver kostenloser Schriftarten und Schriftschnitte an, die alle unter der „öffentlichen Schriftarten Lizenz“ veröffentlicht wurden.', 'https://www.theleagueofmoveabletype.com/', 'teaser_verweis_leagueofmoveabletype.jpg', 'Schriftarten', 'false', 45, ''),
